__NUXT_JSONP__("/about", (function(a,b,c,d,e,f,g){return {data:[{aboutUs:[{_createdAt:"2021-03-04T01:54:00Z",_id:"c9e2c609-ad0b-47cd-98c1-ccf2a65e22ee",_rev:"yXwlT3WstaCz55q6hlu6ZI",_type:e,_updatedAt:"2021-05-09T02:08:02Z",description:[{_key:"eb35cbc49148",_type:b,children:[{_key:"71a52ababaa5",_type:a,marks:[],text:"Chihuahua Rescue & Transport (CRT) is a foster-based rescue group that places Chihuahuas and Chihuahua mixes in approved homes. Our purpose is to extend public education and awareness regarding overpopulation, abuse, neglect, importance of spaying\u002Fneutering, and the tragic, high rates of Chihuahua abandonment and shelter euthanasia. We have placed thousands of Chihuahuas and Chihuahua mixes in approved homes since 1996. CRT requires an application, vet check, reference check and home visit for each adopter. Please Opt to Adopt…Spay\u002FNeuter —\u003E Save Lives"}],markDefs:[],style:c},{_key:"bb538242bf3b",_type:b,children:[{_key:"c88fb44071c80",_type:a,marks:[],text:"CRT does not warehouse dogs in a shelter. As in most rescue groups, our dogs are cared for in healthy, safe, loving foster home environments. CRT foster parents work very hard to be sure that their foster dog feels safe and loved while working on housetraining, crate training, socialization, etc. All our dogs are spayed\u002Fneutered, fully vaccinated, heartworm tested, fecal tested, microchipped, on heartworm preventative and have all other veterinary needs taken care of (such as heartworm treatment, dentals, luxating patellas, etc.) CRT is known, and has always been known, for taking the injured, infirm and elderly Chihuahuas and Chihuahua mixes. Please consider the adoption of one of these dogs as well. Seniors and special needs dogs make great pets too!"}],markDefs:[],style:c},{_key:"91caa2d37a36",_type:b,children:[{_key:"0090ca680d91",_type:a,marks:[],text:"We currently service the following states."}],markDefs:[],style:c},{_key:"48ea1f448937",_type:b,children:[{_key:"4c0afb7555c60",_type:a,marks:[d],text:"Midwest Region"},{_key:"4c0afb7555c61",_type:a,marks:[],text:" (Ohio, Wisconsin, Illinois, Michigan)"}],markDefs:[],style:c},{_key:"b9c6a1ca2a6f",_type:b,children:[{_key:"88b731c543f60",_type:a,marks:[d],text:"Southeast Region"},{_key:"88b731c543f61",_type:a,marks:[],text:" (North Carolina, South Carolina, Virginia, Georgia, Maryland, and West Virginia)"}],markDefs:[],style:c},{_key:"3f9de7633e94",_type:b,children:[{_key:"d3839291ad000",_type:a,marks:[d],text:"Southwest Region"},{_key:"d3839291ad001",_type:a,marks:[],text:" (Texas and Oklahoma)"}],markDefs:[],style:c},{_key:"fe0b8afaa039",_type:b,children:[{_key:"ec5bc27e9937",_type:a,marks:[],text:"Please browse our site to see Chihuahuas currently up for adoption."}],markDefs:[],style:c},{_key:"5de5e17ed028",_type:b,children:[{_key:"7d17eda959a1",_type:a,marks:[d],text:"CRT"},{_key:"549e08400cbe",_type:a,marks:[],text:" is a 501(c)3 nonprofit, public charity. We have no paid employees. We are dedicated to rescue, adoption and the medical care of stray and homeless Chihuahuas in need, and to help control the growing overpopulation through spaying and neutering all dogs in our care. "}],markDefs:[],style:c},{_key:"377d1bad1644",_type:b,children:[{_key:"a8c27c4344ce0",_type:a,marks:[],text:"Full financial information is available from "},{_key:"886a2e3a87bc",_type:a,marks:[d],text:"GuideStar"},{_key:"f8cd79c9f2df",_type:a,marks:[],text:", "},{_key:"1eed1a1e6447",_type:a,marks:[d],text:"The National Database of Nonprofit Organizations"},{_key:"1dbf13f92239",_type:a,marks:[],text:" at "},{_key:"a8c27c4344ce1",_type:a,marks:[f,d],text:"http:\u002F\u002Fwww.guidestar.org"},{_key:"a8c27c4344ce2",_type:a,marks:[],text:". Registration is free but required to view GuideStar reports. CRT is a Texas Domestic Nonprofit Corporation, File Number 800942706. Registration does not imply endorsement, approval or recommendation by the state. To view our financial reports, email our treasurer."}],markDefs:[{_key:f,_type:"link",href:"http:\u002F\u002Fwww.guidestar.org\u002F "}],style:c},{_key:"9a06a70b7f49",_type:b,children:[{_key:"2661e6316ddf0",_type:a,marks:[],text:""}],markDefs:[],style:c}],mainImage:{_type:"image",asset:{_ref:"image-f855d46747650c0da660c5310d6b3ed9bb837251-960x823-jpg",_type:"reference"}},name:"about",slug:{_type:g,current:"about-us"},subtitle:"History of CRT",title:"About Us"}],mission:{_createdAt:"2021-03-13T14:55:08Z",_id:"8b416a7b-7e09-4c8b-8f2b-65c6af844c20",_rev:"kSQupt53peWyxTIQ6wLrmL",_type:e,_updatedAt:"2021-03-13T15:01:46Z",description:[{_key:"81db60c82f89",_type:b,children:[{_key:"8c69c08e6a80",_type:a,marks:[],text:"Chihuahua Rescue Transport (CRT) is a non-profit organization dedicated to saving chihuahuas by placing them in foster homes and supporting them until they are ready to be matched with their forever homes."}],markDefs:[],style:c},{_key:"9c05bb43a4ed",_type:b,children:[{_key:"8c3982665ad2",_type:a,marks:[],text:"CRT "},{_key:"9e2d2c76e4a0",_type:a,marks:[],text:"is a 501(c)3 nonprofit, public charity. We have no paid employees. We are dedicated to rescue, adoption and the medical care of stray and homeless Chihuahuas in need, and to help control the growing overpopulation through spaying and neutering all dogs in our care. "}],markDefs:[],style:c}],name:"mission",slug:{_type:g,current:"our-mission"},title:"Our Mission"}}],fetch:[],mutations:[]}}("span","block","normal","strong","information","2a20f421be89","slug")));