__NUXT_JSONP__("/adopt/buddy", (function(a,b,c,d,e,f,g,h,i,j){return {data:[{chihuahua:null,chihuahuaSE:{_createdAt:"2021-05-09T05:06:12Z",_id:"5cfb55bb-6fea-41e7-b114-204082178633",_rev:"yXwlT3WstaCz55q6hnrhLo",_type:"chihuahuasSE",_updatedAt:"2021-05-09T23:55:55Z",age:"11 years old",chihuahua:f,description:[{_key:"e5dd3d00355b",_type:a,children:[{_key:"2a82053258c7",_type:b,marks:[],text:"Buddy the firedog is 11 years old and 14 lbs of sweet love."}],markDefs:[]},{_key:"1cd9bb484411",_type:a,children:[{_key:"78ed25f28bc80",_type:b,marks:[],text:"As an older gentleman he has limited vision and hearing, but with time to get used to his home he navigates well! He also takes daily medication for his heart, but this guy doesn’t let his challenges stop him from getting lots of cuddles!"}],markDefs:[]},{_key:"8ad7b45a6960",_type:a,children:[{_key:"4b137b506b2d0",_type:b,marks:[],text:"Buddy does well with respectful kids who understand how to approach a vision challenged dog, and gets along with dogs of all sizes as long as they give him time to get used to them!"}],markDefs:[]},{_key:"2953c1a07350",_type:a,children:[{_key:"9a7fec1403350",_type:b,marks:[],text:"Buddy walks well on a leash for short walks, does great with house training as long as his people let him out regularly, and sleeps through the night like the gentleman he is!"}],markDefs:[]},{_key:"9cf3729e9883",_type:a,children:[{_key:"f5be1f6c5d0b0",_type:b,marks:[],text:"He is neutered, UTD on shots, HW neg and on HW prevention."}],markDefs:[]},{_key:"a022d9cc5c60",_type:a,children:[{_key:"e3812f05e4e30",_type:b,marks:[],text:"His adoption donation is $250.00."}],markDefs:[]},{_key:"ac44af0a8e50",_type:a,children:[{_key:"cdebc6005c3e0",_type:b,marks:[],text:"If you are interested in the above dog, please do not email us and tell us that you are interested, but submit an adoption application. The link for the application is below."}],markDefs:[]},{_key:"7031bf240428",_type:a,children:[{_key:"c44dadf371070",_type:b,marks:[],text:"To submit an application go here……chihuahua-rescue.com\u002Fadoption-application\u002F"}],markDefs:[]},{_key:"d409470ae6de",_type:a,children:[{_key:"7bf4e9ca5cb10",_type:b,marks:[],text:"An application must be submitted and approved before a meet-n-greet can be scheduled."}],markDefs:[]},{_key:"8f711fc8bf25",_type:a,children:[{_key:"12d3752064220",_type:b,marks:[],text:"Your current dogs\u002Fcats must be spayed or neutered unless a veterinarian has determined that your current pet(s) should not be fixed due to medical reasons. They must also be up-to-date on shots and on HW preventative."}],markDefs:[]},{_key:"3e7bff919699",_type:a,children:[{_key:"ce21c128202d0",_type:b,marks:[],text:"Reference checks and a home visit are required so SE dogs can only be adopted in NC\u002FSC, VA, TN, AL, MD and GA."}],markDefs:[],style:"h2"}],dogsCats:"Gets along with dogs",gender:g,imageFive:{_type:c,asset:{_ref:"image-30f55ffdd6a4e31c2483de1b4b93ee47cfae2240-768x576-jpg",_type:d}},imageFour:{_type:c,asset:{_ref:"image-17a2de177a91a3ed2de160febc8d285c6811e41f-768x576-jpg",_type:d}},imageSix:{_type:c,asset:{_ref:"image-97d7e4fb4a343f35df8cb8a3214a00e902b49692-768x917-jpg",_type:d}},imageThree:{_type:c,asset:{_ref:"image-0dc5489f2f8c8ac72766b3f2c0ab5afc38aac274-720x540-jpg",_type:d}},imageTwo:{_type:c,asset:{_ref:"image-8816cad4a05cb4484f4558f87d9c35c319d361e2-768x764-jpg",_type:d}},mainImage:{_type:c,asset:{_ref:"image-badc734cb4b129e2a0850cfa1ca17dd49e943ca2-250x250-jpg",_type:d}},order:1,region:"southeast",slug:{_type:h,current:i},status:j,tagline:"A special needs dog with a lot of love to give",weight:"14 lbs"},chihuahuaMW:{_createdAt:"2021-05-09T20:46:55Z",_id:"0a625722-9a58-4238-b605-4f307538a824",_rev:"xXyQsGAB8gaWDns0yNRKt7",_type:"chihuahuasMW",_updatedAt:"2021-05-09T23:57:42Z",age:"Twelve years old",chihuahua:f,description:[{_key:"2175fc302f2e",_type:a,children:[{_key:"7a47d6b43a4d",_type:b,marks:[],text:"Are you looking for a Buddy? You’ve found him! I’m a twelve-year-old, five-pound male chihuahua, fully vetted and vaccinated, housetrained, neutered, microchipped, and on heartworm preventative. I need a new best friend, and I’m hoping you’ll apply for the job!"}],markDefs:[],style:e},{_key:"fd8a450ebc07",_type:a,children:[{_key:"4c02525b1a200",_type:b,marks:[],text:"I have to admit that I can be a bit of a grumpy old man, but can you blame me? My human had to go to a nursing home and couldn’t take me with him, so now I’m sad and lonely. It’s not fair that I have to look for a new home at my age. I’m hoping someone will be able to see past my gruff exterior to the sweet dog beneath!"}],markDefs:[],style:e},{_key:"0324834eef15",_type:a,children:[{_key:"2c12924a8acf0",_type:b,marks:[],text:"I might seem antisocial, but once I get to know you, I’ll be loving and loyal, just like I was with my last human. I might nip at first when you try to pick me up, but you won’t have to worry about that since I don’t have any teeth left. Once we get to know each other, I’ll welcome all the kisses and belly rubs you care to give!"}],markDefs:[],style:e},{_key:"267f5d78e95a",_type:a,children:[{_key:"93f5fb7e3f5f0",_type:b,marks:[],text:"You may not be able to teach an old dog new tricks, but you can give one a new friend and a new lease on life! Would you be a buddy and do that for me?"}],markDefs:[],style:e},{_key:"470e4bed4361",_type:a,children:[{_key:"d84c7f5ab0cd0",_type:b,marks:[],text:"Buddy’s adoption fee is $250. If you are interested in adopting Buddy, please DO NOT SEND A MESSAGE TO OUR RESCUE GROUP. We are too busy placing dogs in great homes to answer messages and inquiries through the listing site."}],markDefs:[],style:e},{_key:"86ed6ea437bc",_type:a,children:[{_key:"5b339e0230220",_type:b,marks:[],text:"CRT requires an approved adoption application and a home visit for all adoption applicants. CRT transports to OH, KY, IL, MI, MN, WI."}],markDefs:[],style:e},{_key:"466b34993155",_type:a,children:[{_key:"f74cf07910620",_type:b,marks:[],text:"\n"}],markDefs:[],style:e}],gender:g,imageTwo:{_type:c,asset:{_ref:"image-876c6cced1b5109e2237b05bfe11dae06725e9a7-768x768-jpg",_type:d}},mainImage:{_type:c,asset:{_ref:"image-6192ccd6d995b3b37361df10654380c62c35fcbe-250x250-jpg",_type:d}},neuteredSpayed:"Yes",order:6,region:"midwest",slug:{_type:h,current:i},status:j,tagline:"Will you be my new person?",weight:"Five pounds"}}],fetch:[],mutations:[]}}("block","span","image","reference","normal","Buddy","male","slug","buddy","Available")));