__NUXT_JSONP__("/adopt/destiny", (function(a,b,c,d,e,f){return {data:[{chihuahua:f,chihuahuaSE:f,chihuahuaMW:{_createdAt:"2021-05-09T20:57:12Z",_id:"c3802516-fc61-4846-99b9-0d171521056c",_rev:"yXwlT3WstaCz55q6hnrms0",_type:"chihuahuasMW",_updatedAt:"2021-05-09T23:57:16Z",age:"13.5 years old",chihuahua:"Destiny",children:"Not good with small children",description:[{_key:"84fea5ac8a3b",_type:a,children:[{_key:"c893aeb7f222",_type:b,marks:[],text:"Hi, I’m Destiny, but you can call me Miss Independent! I am 13.5 years young (born April 2007) and I weigh ten pounds. CRT has made sure that I am up to date on vaccines, had my heartworm test, got my microchip, and take my monthly heartworm preventative."}],markDefs:[],style:c},{_key:"cee2b83d5023",_type:a,children:[{_key:"20c8d50fa5440",_type:b,marks:[],text:"I get along well with other small dogs as long as they are docile, but I am too feisty for larger dogs, cats, or small children. I’m sweet, but I have a sassy side and will give you a little warning nip if I need my personal space. I prefer to dine alone so I can enjoy my food in peace, away from the other dogs."}],markDefs:[],style:c},{_key:"0ca68353c236",_type:a,children:[{_key:"2de11dc3fac90",_type:b,marks:[],text:"As an older gal, I’ve earned my share of pampering! I like to be carried up and down the stairs and will wait patiently for you to carry me across hard flooring to the carpet I prefer. I require a strict potty schedule to avoid accidents in the house. I’m mostly crate trained, but I don’t like to be left alone for long periods of time."}],markDefs:[],style:c},{_key:"fa9c9e49af15",_type:a,children:[{_key:"334502554db30",_type:b,marks:[],text:"I have a loud bark, and I’m not afraid to speak my mind if I hear loud noises or see neighbors in the yard! I’m told I’m a bit hard of hearing, so my foster family supervises me in the yard to keep me safe. I love it when they let me explore outside or take me for walks or car rides."}],markDefs:[],style:c},{_key:"aee308df14cc",_type:a,children:[{_key:"c4b2501a7eb10",_type:b,marks:[],text:"I might take a little time to warm up to you, but once I do, I will be a loyal lap warmer and will curl up at the foot of your bed for a cozy snooze. I’m currently living in Wixom, Michigan, but I’d love to live out the rest of my years with you!"}],markDefs:[],style:c},{_key:"5449f53fd604",_type:a,children:[{_key:"bbe2db9b3ec90",_type:b,marks:[],text:"Destiny’s adoption fee is $250. If you are interested in adopting Destiny, please DO NOT SEND A MESSAGE TO OUR RESCUE GROUP. We are too busy placing dogs in great homes to answer messages and inquiries through the listing site."}],markDefs:[],style:c},{_key:"ebc8af669faa",_type:a,children:[{_key:"87d28b7f50d40",_type:b,marks:[],text:"CRT requires an approved adoption application and a home visit for all adoption applicants. CRT transports to OH, KY, IL, MI, MN, WI."}],markDefs:[],style:c},{_key:"d095aea90c25",_type:a,children:[{_key:"8cd7d09017650",_type:b,marks:[],text:"\n"}],markDefs:[],style:c}],dogsCats:"Good with small dogs \u002F Not cats",gender:"female",imageThree:{_type:d,asset:{_ref:"image-0da1b25c0b277fb7ab38e504b753d5f662955648-372x372-jpg",_type:e}},imageTwo:{_type:d,asset:{_ref:"image-f4dd854d476dfb72c80b72162e35e08dc1d253a6-380x507-jpg",_type:e}},mainImage:{_type:d,asset:{_ref:"image-623a42dfadd6f5875c0971876ad72d6621add141-250x250-jpg",_type:e}},order:3,region:"midwest",slug:{_type:"slug",current:"destiny"},status:"Available",tagline:"A loyal lap warmer and a little bit feisty",weight:"10 pounds"}}],fetch:[],mutations:[]}}("block","span","normal","image","reference",null)));