__NUXT_JSONP__("/adopt/elvis", (function(a,b,c,d,e,f){return {data:[{chihuahua:f,chihuahuaSE:f,chihuahuaMW:{_createdAt:"2021-05-09T21:05:32Z",_id:"fe80d55d-db0c-4987-86a1-f88d19d7fdec",_rev:"yXwlT3WstaCz55q6hnrjci",_type:"chihuahuasMW",_updatedAt:"2021-05-09T23:56:24Z",age:"Six years old",chihuahua:"Elvis",description:[{_key:"4da6f58dad8d",_type:a,children:[{_key:"e5261b50d1ee",_type:b,marks:[],text:"Hello, baby, it’s me, Elvis! I ain’t nothin’ but a chihuahua, a nine-pound, six-year-old male chihuahua, fully vetted, neutered, vaccinated, microchipped, and on heartworm medication. If you’re looking for someone to love you tender, my sister Elsa and I would like to audition for the role!"}],markDefs:[],style:c},{_key:"bf8eb9e0f3cd",_type:a,children:[{_key:"7aafbd6db00b0",_type:b,marks:[],text:"You won’t be surprised that, with a name like Elvis, I love the spotlight! With my beautiful, long white hair, I get all the attention I crave, no sequins required. Unfortunately, I had a flea infestation before coming to the rescue, and some of my gorgeous fur had to be shaved. Don’t worry, baby, the fleas are gone, and the fur will all grow back!"}],markDefs:[],style:c},{_key:"6e77018462b4",_type:a,children:[{_key:"89d81e169bbf0",_type:b,marks:[],text:"I take after that other Elvis in another way too, my love of food! I don’t like to share my food, so I’m better off eating alone so I don’t get aggressive. Eating is the only thing I want to do alone though. I’m very close to my sister, Elsa, so she’ll need to be adopted along with me!"}],markDefs:[],style:c},{_key:"1e83603661da",_type:a,children:[{_key:"a02401a928080",_type:b,marks:[],text:"So what are you waiting for? Throw on those blue suede shoes, fill out an application, and get ready for a whole lot of burnin’ love! Thank you, thank you very much!"}],markDefs:[],style:c},{_key:"ebc68ddfca67",_type:a,children:[{_key:"808b42368ab00",_type:b,marks:[],text:"Elvis and Elsa’s adoption fees are $300 each. If you are interested in adopting Elvis and Elsa, please DO NOT SEND A MESSAGE TO OUR RESCUE GROUP. We are too busy placing dogs in great homes to answer messages and inquiries through the listing site."}],markDefs:[],style:c},{_key:"559ac443c25c",_type:a,children:[{_key:"76a236ff16b30",_type:b,marks:[],text:"PLEASE GO TO THE LINK BELOW TO COMPLETE AN ADOPTION APPLICATION."}],markDefs:[],style:c},{_key:"6a3170e9d55d",_type:a,children:[{_key:"1105fe2846c80",_type:b,marks:[],text:"CRT requires an approved adoption application and a home visit for all adoption applicants. CRT transports to OH, KY, IL, MI, MN, WI."}],markDefs:[],style:c}],gender:"male",imageThree:{_type:d,asset:{_ref:"image-2cbcecfc77be4eb414a9fbee9f23ce22c02ee501-768x768-jpg",_type:e}},imageTwo:{_type:d,asset:{_ref:"image-fd0abd970d2866b2e39820559d2e6efd7e6f0cc3-768x768-jpg",_type:e}},mainImage:{_type:d,asset:{_ref:"image-613fda4f68228eacafd18f10ebfb473ea487c441-250x250-jpg",_type:e}},neuteredSpayed:"Yes",order:0,region:"midwest",slug:{_type:"slug",current:"elvis"},status:"Available",tagline:"With a name like Elvis, I love the spotlight!",weight:"9 pounds"}}],fetch:[],mutations:[]}}("block","span","normal","image","reference",null)));